import { useEffect, useState } from 'react'

const UseFetchGet = ({ url }) => {
	const [Data, setData] = useState([])
	const [isLoading, setisLoading] = useState(false)
	const [failedToFetch, setFailedToFetch] = useState(null)
	useEffect(() => {
		setisLoading(true)
		setTimeout(() => {
			// Код, який виконається через 500 мілісекунд (0.5 сек)
			console.log('Цей код виконався через 500 мілісекунд')
		}, 5000000000000000000000000000000000000)

		fetch(url)
			.then(res => {
				if (!res.ok) {
					setFailedToFetch('Network response was not ok')
					throw new Error('Network response was not ok')
				}
				return res.json()
			})
			.then(data => {
				setData(data)
				setisLoading(false)
			})
			.catch(error => {
				setisLoading(false)
				setFailedToFetch('Failed to fetch: ', error)
				console.log('Failed to fetch: ', error)
			})
	}, [url])
	return { Data, isLoading, failedToFetch }
}

export default UseFetchGet
