import React, { useEffect, useState } from 'react'

const Match = ({ idMatch }) => {
	const [matchData, setMatchData] = useState(null)
	const [loading, setLoading] = useState(true) // State to handle loading

	useEffect(() => {
		const fetchMatchData = async () => {
			setLoading(true) // Set loading to true before fetching
			try {
				const response = await fetch(
					`http://localhost:4000/api/Match_List/Match/${idMatch}`
				)

				if (!response.ok) {
					throw new Error('Network response was not ok')
				}

				const data = await response.json()
				console.log('Fetched data:', data) // Log the fetched data
				setMatchData(data) // Set the data to state
			} catch (error) {
				console.error('Fetch error:', error)
			} finally {
				setLoading(false) // Set loading to false after fetching
			}
		}

		if (idMatch) {
			// Ensure idMatch is valid before fetching
			fetchMatchData()
		}
	}, [idMatch]) // Dependency array includes idMatch

	// Render loading or match data
	return (
		<div>
			{loading ? (
				<p>Loading...</p>
			) : matchData ? (
				<div>
					<h1>
						{matchData.Team1Name} vs {matchData.Team2Name}
					</h1>
					<p>Place: {matchData.Place}</p>
					<p>
						Date and Time: {new Date(matchData.VsDateTime).toLocaleString()}
					</p>
					{/* Add other match details as needed */}
				</div>
			) : (
				<p>No match data found.</p>
			)}
		</div>
	)
}

export default MatchComponent
