import { GoogleLogin } from '@react-oauth/google'
import { useNavigate } from 'react-router-dom'
import { useAuth } from '../context/AuthContext'

export default function Logon() {
	const navigate = useNavigate()
	const { setIsRegUser, setUser } = useAuth()

	const responseSucess = async response => {
		const { credential } = response

		fetch('http://localhost:4000/api/google-login', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				token: credential,
			}),
		})
			.then(response => response.json())
			.then(data => {
				console.log('Login successful:', data)
			})
			.catch(error => {
				console.error('Error during login:', error)
			})
		if (response.ok) {
			console.log('Успішний вхід')
			localStorage.setItem('token', data.token)
			console.log('Вхід успішний:', data.message)
			const decoded = jwtDecode(data.token)
			setIsRegUser(true)
			setUser(decoded)
			navigate('/Home')
		} else {
			console.log('Помилка:', data.message)
			if (data.message && data.message.includes('Email not found')) {
				setEmailError('Email не знайдено.')
			} else if (data.message && data.message.includes('Incorrect password')) {
				setPasswordError('Неправильний пароль.')
			} else {
				console.error('Помилка на сервері:', data)
				setGeneralError('Помилка на сервері. Спробуйте пізніше.')
			}
		}
	}
	const responseFailure = () => {
		console.log('login is not successful')
	}

	return (
		<div className='App'>
			<GoogleLogin
				clientId='500804855419-pms6km4isevbtq88rpgbpp02tdjq26fm.apps.googleusercontent.com'
				onSuccess={responseSucess}
				onFailure={responseFailure}
			/>
		</div>
	)
}
