import { ThumbsDown, ThumbsUp } from 'lucide-react'
import React, { useState } from 'react'

const LikesDisslikes = ({ OneComment }) => {
	const [likes, setLikes] = useState(OneComment.likes)
	const [dislikes, setDislikes] = useState(OneComment.dislikes)

	const [onlikes, setOnlikes] = useState(false)
	const [ondislikes, setOndislikes] = useState(false)

	const handleLikes = e => {
		if (onlikes) {
			setLikes(likes - 1)
			setOnlikes(true)
		} else {
			setLikes(likes + 1)
			setOnlikes(false)
		}
	}
	const handleDislikes = e => {
		if (ondislikes) {
			setDislikes(dislikes - 1)
			setOndislikes(false)
		} else {
			setDislikes(dislikes + 1)
			setOndislikes(true)
		}
	}
	return (
		<div className='likes-dislikes flex mt-2'>
			<div className='flex'>
				{onlikes ? (
					<ThumbsUp
						onClick={handleLikes}
						className='ml-2 text-gray-700 animate-pulse cursor-pointer h-4 mt-[2px]'
					/>
				) : (
					<ThumbsUp
						onClick={handleLikes}
						className='ml-2 text-gray-400  cursor-pointer h-4 mt-[2px]'
					/>
				)}

				{likes}
			</div>
			<div className='flex '>
				{}
				<ThumbsDown
					onClick={handleDislikes}
					className='ml-2 text-gray-400  h-4 mt-1'
				/>
				{dislikes}
			</div>
		</div>
	)
}

export default LikesDisslikes
