import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { NewsDate } from '../js/TimeValidation'

const NewsAside = () => {
	const [OneNews, SetOneNews] = useState(null)
	const [failedToFetch, setFailedToFetch] = useState(false)

	useEffect(() => {
		fetch(`http://localhost:4000/api/news/news_last`)
			.then(response => {
				if (!response.ok) {
					throw new Error('Network response was not ok')
				}
				return response.json()
			})
			.then(data => {
				if (data.length > 0) {
					SetOneNews(data[0])
				}
				setFailedToFetch(false)
			})
			.catch(err => {
				console.error(err)
				setFailedToFetch(true)
			})
	}, [])
	if (!OneNews && !failedToFetch) {
		return <p>Завантаження...</p>
	}

	if (failedToFetch) {
		return <p>Не вдалося завантажити новину.</p>
	}
	return (
		<aside className='mr-4 my-5 w-[25%] rounded-xl'>
			<h3 className='font-bold my-5'>Останні новини</h3>
			<div className=''>
				<div className='flex'>
					<Link to='#' className='text-xs'>
						{OneNews.gameName}
					</Link>
					<span className='ml-3 w-px h-4 bg-gray-400 mr-4 '></span>
					<p className='mr-4 text-gray-400 text-xs'>
						{' '}
						{NewsDate(OneNews.publish_date)}
					</p>
				</div>
				<div>
					<Link className='text-base underline'>{OneNews.title}</Link>
				</div>
			</div>
		</aside>
	)
}

export default NewsAside
