import { Lock, Mail, UserRound } from 'lucide-react'
import { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import CustomForm from '../styles/CustomForm.module.css'
import styles from '../styles/RegistrationLogin.module.css'

export function Registration() {
	const [Email, setEmail] = useState('')
	const [Password, setPassword] = useState('')
	const [RePassword, setRePassword] = useState('')
	const [First_Name, setFirst_Name] = useState('')
	const [Last_Name, setLast_Name] = useState('')
	const [Conditions, setConditions] = useState('')
	const [FormValid, setFormValid] = useState(false)

	const [EmailDirty, setEmailDirty] = useState(false)
	const [PasswordDirty, setPasswordDirty] = useState(false)
	const [RePasswordDirty, setRePasswordDirty] = useState(false)
	const [First_NameDirty, setFirst_NameDirty] = useState(false)
	const [Last_NameDirty, setLast_NameDirty] = useState(false)
	const [ConditionsDirty, setConditionsDirty] = useState(false)

	const [EmailError, setEmailError] = useState('Email не може бути пустим')
	const [PasswordError, setPasswordError] = useState(
		'Password не може бути пустим'
	)
	const [RePasswordError, setRePasswordError] = useState(
		'RePassword не може бути пустим'
	)
	const [First_NameError, setFirst_NameError] = useState(
		'First Name не може бути пустим'
	)
	const [Last_NameError, setLast_NameError] = useState(
		'Last Name не може бути пустим'
	)
	const [ConditionsError, setConditionsError] = useState('')

	const blurHandler = e => {
		switch (e.target.name) {
			case 'Email':
				setEmailDirty(true)
				break
			case 'Password':
				setPasswordDirty(true)
				break
			case 'RePassword':
				setRePasswordDirty(true)
				break
			case 'First_Name':
				setFirst_NameDirty(true)
				break
			case 'Last_Name':
				setLast_NameDirty(true)
				break
			case 'Last_Name':
				setConditionsDirty(true)
				break
			default:
				break
		}
	}

	const EmailHandler = e => {
		setEmail(e.target.value)

		const re =
			/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@(([^<>()[\]\\.,;:\s@"]+\.)+[^<>()[\]\\.,;:\s@"]{2,})$/i

		if (!re.test(String(e.target.value).toLowerCase())) {
			setEmailError('Email неправильний')
		} else {
			setEmailError('')
		}
	}

	const PasswordHandler = e => {
		setPassword(e.target.value)
		const re =
			/^(?![!@#$%^&*])[a-zA-Z0-9!@#$%^&*]*[0-9]+[a-zA-Z0-9]*[!@#$%^&*]+[a-zA-Z0-9]*$/

		if (!e.target.value) {
			setPasswordError('Password не може бути пустим')
		} else if (e.target.value.length < 6 || e.target.value.length > 16) {
			setPasswordError('Password має бути від 6 до 16 символів')
		} else if (!re.test(String(e.target.value))) {
			setPasswordError(
				'Пароль не може починатися зі спеціального символу, але повинен містити хоча б одну цифру і один спеціальний символ (!@#$%^&*)'
			)
		} else {
			setPasswordError('')
		}
	}

	const RePasswordHandler = e => {
		setRePassword(e.target.value)

		if (!e.target.value) {
			setRePasswordError('RePassword не може бути пустим')
		} else if (e.target.value !== Password) {
			setRePasswordError('Паролі не збігаються')
		} else {
			setRePasswordError('')
		}
	}

	const ConditionsHandler = e => {
		setConditions(e.target.value)

		if (!e.target.value) {
			setConditionsError(' vsdfgyjasgkj.')
		} else {
			setConditionsError('')
		}
	}

	const Last_NameHandler = e => {
		setLast_Name(e.target.value)

		const re = /^[A-ZА-ЯІЇЄ][a-zа-яіїє]+(?: [A-ZА-ЯІЇЄ][a-zа-яіїє]+)?$/

		if (!re.test(e.target.value)) {
			setLast_NameError(
				'Прізвище повинно містити тільки літери, перша літера мaє бути великою, а решта — маленькими, і можливий один пробіл між словами (якщо прізвище подвійне).'
			)
		} else {
			setLast_NameError('')
		}
	}
	const First_NameHandler = e => {
		setFirst_Name(e.target.value)

		const re = /^[A-ZА-ЯІЇЄ][a-zа-яіїє]+(?: [A-ZА-ЯІЇЄ][a-zа-яіїє]+)?$/

		if (!re.test(e.target.value)) {
			setFirst_NameError(
				"Ім'я повинно містити тільки літери, перша літера мaє бути великою, а решта — маленькими, і можливий один пробіл між словами (якщо ім'я подвійне)."
			)
		} else {
			setFirst_NameError('')
		}
	}

	useEffect(() => {
		if (
			EmailError ||
			PasswordError ||
			RePasswordError ||
			First_NameError ||
			Last_NameError ||
			!Email ||
			!Password ||
			!RePassword ||
			!First_Name ||
			!Last_Name
		) {
			setFormValid(false)
		} else {
			setFormValid(true)
		}
	}, [
		EmailError,
		PasswordError,
		RePasswordError,
		First_NameError,
		Last_NameError,
		Email,
		Password,
		RePassword,
		First_Name,
		Last_Name,
	])

	const handleSubmit = e => {
		e.preventDefault()
		if (FormValid) {
			// Обробка реєстрації
			console.log('Форма відправлена')
		}
	}

	return (
		<div className={styles.Container}>
			<div className={styles.RightBlock}></div>

			<div className={styles.LeftBlock}>
				<div className={styles.RegForm}>
					<div className={styles.RegFormLine}></div>
					<form onSubmit={handleSubmit} noValidate>
						<h1 className={styles.RegFormHeader}>Registration Form</h1>

						{/* Email Field */}
						<div className={styles.RegFormFullBlock}>
							{EmailDirty && EmailError && (
								<div className={styles.RegFormError}>{EmailError}</div>
							)}
							<div className={styles.RegFormBlock}>
								<Mail />
								<input
									className={CustomForm.CustomInput}
									type='email'
									name='Email'
									value={Email}
									placeholder='Email'
									onBlur={blurHandler}
									onChange={EmailHandler}
									required
								/>
							</div>
						</div>

						{/* Password Field */}
						<div className={styles.RegFormFullBlock}>
							{PasswordDirty && PasswordError && (
								<div className={styles.RegFormError}>{PasswordError}</div>
							)}
							<div className={styles.RegFormBlock}>
								<Lock />
								<input
									className={CustomForm.CustomInput}
									type='password'
									name='Password'
									value={Password}
									onBlur={blurHandler}
									onChange={PasswordHandler}
									placeholder='Password'
									required
								/>
							</div>
						</div>

						{/* RePassword Field */}
						<div className={styles.RegFormFullBlock}>
							{RePasswordDirty && RePasswordError && (
								<div className={styles.RegFormError}>{RePasswordError}</div>
							)}
							<div className={styles.RegFormBlock}>
								<Lock />
								<input
									className={CustomForm.CustomInput}
									type='password'
									name='RePassword'
									value={RePassword}
									onBlur={blurHandler}
									onChange={RePasswordHandler}
									placeholder='Re-type password'
									required
								/>
							</div>
						</div>

						{/* First Name and Last Name*/}
						<div className={styles.RegFormFullBlock}>
							{First_NameDirty && First_NameError && (
								<div className={styles.RegFormError}>{First_NameError}</div>
							)}
							{Last_NameDirty && Last_NameError && (
								<div className={styles.RegFormError}>{Last_NameError}</div>
							)}
							<div className={styles.RegFormNamesBlock}>
								<div
									className={`${styles.RegFormBlock} ${styles.RegFormBlockNames}`}
								>
									<UserRound />
									<input
										className={CustomForm.CustomInput}
										type='text'
										name='First_Name'
										value={First_Name}
										onBlur={blurHandler}
										onChange={First_NameHandler}
										placeholder='First Name'
										required
									/>
								</div>
								<div
									className={`${styles.RegFormBlock} ${styles.RegFormBlockNames}`}
								>
									<UserRound />
									<input
										className={CustomForm.CustomInput}
										type='text'
										name='Last_Name'
										value={Last_Name}
										onBlur={blurHandler}
										onChange={Last_NameHandler}
										placeholder='Last Name'
										required
									/>
								</div>
							</div>
						</div>

						{/* Terms and Conditions */}
						<div className={styles.RegFormFullBlock}>
							{First_NameDirty && First_NameError && (
								<div className={styles.RegFormError}>{First_NameError}</div>
							)}
							<div
								className={`${styles.RegFormBlock} ${styles.RegFormCheckbox}`}
							>
								<div>
									<input
										type='checkbox'
										id='conditions'
										name='conditions'
										onChange={ConditionsHandler}
										required
									/>
									<label htmlFor='conditions'>
										I agree with terms and conditions
									</label>
								</div>
							</div>
						</div>

						{/* Newsletter Subscription */}
						<div className={`${styles.RegFormBlock} ${styles.RegFormCheckbox}`}>
							<div>
								<input type='checkbox' id='newsletter' name='newsletter' />
								<label htmlFor='newsletter'>
									I want to receive the newsletter
								</label>
							</div>
						</div>

						{/* Submit Button */}
						<div className={`${styles.RegFormBlock} ${styles.RegFormButton}`}>
							<button
								type='submit'
								disabled={!FormValid}
								className={styles.CustomButtonSubmit}
							>
								Register
							</button>
						</div>
					</form>

					{/* Link to Login */}
					<div className={styles.AfterForm}>
						<Link to={'/Login'}>Already have an account?</Link>
					</div>
				</div>
			</div>
		</div>
	)
}
