import { UserRound } from 'lucide-react'
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { NavBar } from '../components/NavBar'
import { NotAuthorized } from '../components/NotAuthorized'
import { useAuth } from '../context/AuthContext'
import { formatDate } from '../js/TimeValidation'
import style from '../styles/Profile.module.css'

export function Profile() {
	const navigate = useNavigate()
	const { user, isRegUser } = useAuth()
	const [UserProfile, setUserProfile] = useState(null)
	const [formData, setFormData] = useState({
		firstName: '',
		lastName: '',
		date_of_birth: '',
		gender: '',
		email: '',
		documentNumber: '',
		phone: '',
		country: '',
		password: '',
	})
	const [message, setMessage] = useState('')

	const [date_of_birthDirty, setdate_of_birthDirty] = useState(false)
	const [gender, setgenderDirty] = useState(false)

	// Оновлюємо UserProfile, коли user змінюється
	useEffect(() => {
		if (user) {
			setUserProfile(user)
			setFormData({
				firstName: user.first_name,
				lastName: user.last_name,
				date_of_birth: formatDate(user.date_of_birth),
				gender: user.gender,
				email: user.email,
				created_at: user.created_at,
				phone: user.phone_number,
				country: user.country,
				password: '',
			})
		}
	}, [user])
	const handleBlur = e => {
		switch (e.target.name) {
			case 'gender':
				setgenderDirty(true)
				break
			case 'date_of_birth':
				setdate_of_birthDirty(true)
				break
			default:
				break
		}
	}
	const handleChange = e => {
		const { name, value } = e.target
		setFormData(prevState => ({
			...prevState,
			[name]: value,
		}))
	}

	const handleSave = async e => {
		e.preventDefault()
		// Логіка для збереження змін
		const updatedData = {
			first_name: formData.firstName,
			last_name: formData.lastName,
			date_of_birth: formData.date_of_birth,
			gender: formData.gender,
			email: formData.email,
			created_at: formData.created_at,
			phone: formData.phone,
			country: formData.country,
		}

		try {
			const response = await fetch('http://localhost:4000/api/updateProfile', {
				method: 'PUT',
				headers: {
					'Content-Type': 'application/json',
					Authorization: `Bearer ${localStorage.getItem('token')}`,
				},
				body: JSON.stringify(updatedData),
			})

			if (response.ok) {
				const data = await response.json()
				console.log('Профіль успішно оновлено:', data)
				setMessage('Профіль успішно оновлено!')

				setUserProfile(data)
			} else {
				const errorData = await response.json()
				console.error('Помилка оновлення профілю:', errorData.message)
				setMessage('Помилка оновлення профілю.')
			}
		} catch (error) {
			console.error("Помилка з'єднання:", error)
			setMessage("Помилка з'єднання з сервером.")
		}
	}

	if (!isRegUser) {
		;<NotAuthorized />
	}

	if (!UserProfile) {
		return <div>Завантаження даних профілю...</div>
	}

