import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'

const NewsList = () => {
	const [News, SetNews] = useState([])
	const [failedToFetch, setFailedToFetch] = useState(false)
	useEffect(() => {
		fetch('http://localhost:4000/api/news/news_list')
			.then(response => {
				if (!response.ok) {
					throw new Error('Network response was not ok')
				}
				return response.json() // Перетворюємо відповідь на JSON
			})
			.then(data => {
				SetNews(data)
				setFailedToFetch(false)
			})
			.catch(err => {
				console.error(err)
				setFailedToFetch(false)
			})
	}, [])
	console.log(News)
	return (
		<>
			{News.map(OneNews => {
				;<div className='flex w-[80%] h-[120px] my-5'>
					<img
						src='../img/BgErrorPage.jpg'
						className='w-auto h-32 rounded-md'
						alt=''
					/>

					<div className='ml-5'>
						<div className='flex'>
							<Link to='#' className='text-xs'>
								CS2
							</Link>
							<span class='ml-3 w-px h-4 bg-gray-400 mr-4 '></span>
							<p className='mr-4 text-gray-400 text-xs'>
								13 жовтня 2024, 12:14
							</p>
						</div>
						<div className='my-3  '>
							<Link to='Content' className='text-base   underline'>
								{OneNews.title}
							</Link>
							<p className='text-sm mt-4'>
								Дивіться початок матчу 13 жовтня о 17:45 за київським часом
							</p>
						</div>
					</div>
				</div>
			})}
		</>
	)
}

export default NewsList
