import { useEffect, useState } from 'react'
import { BurgerMenu } from '../components/BurgerMenu'
import { Footer } from '../components/Footer'
import { NavBar } from '../components/NavBar'
import useFetchGet from '../hooks/fetch/useFetchGet'

import MyLoader from '../components/Loader'
import { useAuth } from '../context/AuthContext'
import rootstyle from '../styles/root.module.css'

const Notifications = () => {
	const { user } = useAuth()
	const [Notifications, setNotification] = useState([])
	const { Data, isLoading, failedToFetch } = useFetchGet({
		url: `http://localhost:4000/api/notifications/${user.id}`,
	})

	useEffect(() => {
		if (Data) {
			setNotification(Data)
		}
	}, [user])

	if (isLoading) {
		return <MyLoader />
	}
	if (failedToFetch || !user) {
		// Додано перевірку на користувача
		return <div>Failed to fetch notifications</div>
	}

	if (Notifications.length === 0) {
		return <div>No notifications available.</div> // Повідомлення, якщо немає сповіщень
	}

	return (
		<div className={rootstyle.wrapper}>
			<NavBar />

			<div className={rootstyle.Container}>
				<BurgerMenu />

				<main className={rootstyle.Main}>
					{Notifications.map(Notification => (
						<div
							className='w-full min-h-[100px] h-[120px] bg-gray-800 text-gray-200 flex gap-4 items-center justify-evenly p-4 rounded-lg my-5'
							key={stake.id}
						>
							<div>{Notification.id}</div>
							<div>{Notification.match_id}</div>
							<div className='min-w-[20%] flex flex-col items-center'>
								<div className='text-white text-3xl'>₴</div>
								<div className='flex'>
									<div>₴ </div>
									<div>x </div>
								</div>
							</div>
							<div className='flex flex-col justify-evenly items-center'>
								<div></div>
								<div>{formatDate(Notification.created_at)}</div>
								<div>Status: {Notification.status}</div>
							</div>
						</div>
					))}
				</main>
			</div>
			<Footer />
		</div>
	)
}

export default Notifications
