import { House, Menu } from 'lucide-react'
import { useMenu } from '../components/MenuContext' // Adjust the import path as needed
import style from '../styles/BurgerMenu.module.css'

export function BurgerMenu() {
	const { isOpen, setIsOpen } = useMenu() // Access context values

	const toggleMenu = () => {
		setIsOpen(prev => !prev) // Toggle menu visibility
	}

	return (
		<aside
			className={isOpen ? style.AsideContainer : style.AsideContainerHidden}
		>
			<div className={style.AsideBlockBrowse} onClick={toggleMenu}>
				<div className={style.AsideBlockIconBrowse}>
					<Menu />
				</div>
			</div>
			{isOpen && (
				<>
					{/* Menu items */}
					<div className={style.AsideBlock}>
						<div className={style.AsideBlockIcon}>
							<House />
						</div>
						<div className={style.AsideBlockText}>
							<p>Головна</p>
						</div>
					</div>
					{/* Add other menu items here */}
				</>
			)}
		</aside>
	)
}
