import { ThumbsDown, ThumbsUp } from 'lucide-react'
import React, { useEffect, useState } from 'react'
import { useAuth } from '../context/AuthContext'

const LikesDisslikes = ({ OneComment }) => {
	const { user } = useAuth()
	const [likes, setLikes] = useState(OneComment.likes)
	const [dislikes, setDislikes] = useState(OneComment.dislikes)
	const [onlikes, setOnlikes] = useState(false)
	const [onUpdated, setOnUpdated] = useState(false)
	const [ondislikes, setOndislikes] = useState(false)

	// Функція для отримання стану лайків
	const fetchCommentAction = async (commentId, userId) => {
		try {
			const response = await fetch(
				`http://localhost:4000/api/comments/news_comments/${commentId}/${userId}/like`,
				{
					method: 'GET',
					headers: {
						'Content-Type': 'application/json',
					},
				}
			)

			if (!response.ok) {
				throw new Error('Network response was not ok')
			}

			const data = await response.json()
			// Якщо дані містять інформацію про лайк, встановлюємо onlikes в true
			if (data && data.length > 0) {
				setOnlikes(true)
			} else {
				setOnlikes(false) // Встановлюємо onlikes в false, якщо лайка немає
			}
		} catch (error) {
			console.error('Error fetching comment action:', error)
		}
	}

	useEffect(() => {
		if (OneComment.id && user) {
			fetchCommentAction(OneComment.id, user.id)
		}
	}, [OneComment.id, user]) // Викликаємо fetchCommentAction при зміні OneComment.id або user

	// Оновлення лайків на сервері
	const updateLikes = async () => {
		try {
			const response = await fetch(
				`http://localhost:4000/api/comments/news_comments/updateLikes`,
				{
					method: 'PUT',
					headers: {
						'Content-Type': 'application/json',
					},
					body: JSON.stringify({
						commentId: OneComment.id,
						userId: user.id,
						likes,
					}),
				}
			)

			if (!response.ok) {
				throw new Error('Не вдалося оновити лайки/дизлайки на сервері')
			}
		} catch (error) {
			console.error('Помилка при оновленні лайків/дизлайків:', error)
		}
	}

	useEffect(() => {
		// Оновлення лайків тільки коли likes змінюються
		updateLikes()
	}, [OnUpdated]) // Залежність від likes

	const handleLikes = () => {
		if (onlikes) {
			setLikes(likes - 1) // Віднімемо лайк, якщо вже поставлено
			setOnlikes(false)
			setOnUpdated(true)
		} else {
			setLikes(likes + 1) // Додамо лайк
			setOnlikes(true)
			setOnUpdated(true)
			if (ondislikes) {
				setDislikes(dislikes - 1) // Якщо дизлайк поставлений, знімемо його
				setOndislikes(false)
			}
		}
	}

	const handleDislikes = () => {
		if (ondislikes) {
			setDislikes(dislikes - 1) // Віднімемо дизлайк, якщо вже поставлено
			setOndislikes(false)
		} else {
			setDislikes(dislikes + 1) // Додамо дизлайк
			setOndislikes(true)
			if (onlikes) {
				setLikes(likes - 1) // Якщо лайк поставлений, знімемо його
				setOnlikes(false)
			}
		}
	}

	return (
		<div className='likes-dislikes flex mt-2'>
			<div
				onClick={handleLikes}
				className='flex no-select select-none cursor-pointer'
			>
				{onlikes ? (
					<ThumbsUp className='ml-2 text-gray-700 animate-pulse h-4 mt-[2px]' />
				) : (
					<ThumbsUp className='ml-2 text-gray-400 h-4 mt-[2px]' />
				)}
				{likes}
			</div>
			<div
				onClick={handleDislikes}
				className='ml-3 flex select-none cursor-pointer'
			>
				{ondislikes ? (
					<ThumbsDown className='ml-2 text-gray-700 animate-pulse h-4 mt-[2px]' />
				) : (
					<ThumbsDown className='ml-2 text-gray-400 h-4 mt-[2px]' />
				)}
				{dislikes}
			</div>
		</div>
	)
}

export default LikesDisslikes
