const fetchCommentAction = async (commentId, userId, type) => {
	try {
		const response = await fetch(
			`http://localhost:4000/api/comments/news_comments/${commentId}/${userId}/${type}`,
			{
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
				},
			}
		)

		if (!response.ok) {
			throw new Error('Network response was not ok')
		}

		const data = await response.json()
		// Встановлюємо onlikes в true, якщо лайк активний
		if (data && data.length > 0) {
			return true
		} else {
			return false // Встановлюємо onlikes в false, якщо лайка немає
		}
	} catch (error) {
		console.error('Error fetching comment action:', error)
	}
}
