const fetchUpd = async ({ url, value, ...props }) => {
	let loading = true
	let error = null

	try {
		const response = await fetch(url, {
			method: 'PUT',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				value,
				...props,
			}),
		})

		if (!response.ok) {
			throw new Error('Не вдалося оновити лайки/дизлайки на сервері')
		}
	} catch (err) {
		error = err.message
		console.error('Помилка при оновленні лайків/дизлайків:', err)
	} finally {
		loading = false
	}

	return { loading, error }
}

export default fetchUpd
