export const blurHandler = e => {
	switch (e.target.name) {
		case 'Email':
			setEmailDirty(true)
			break
		case 'Password':
			setPasswordDirty(true)
			break
	}
}
export const EmailHandler = e => {
	setEmail(e.target.value)

	const re =
		/^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i

	if (!re.test(String(e.target.value).toLowerCase())) {
		setEmailError('Email не правильний')
	} else {
		setEmailError('')
	}
}
export const PasswordHandler = e => {
	setPassword(e.target.value)
	if (e.target.value.length < 4 || e.target.value.length > 64) {
		setPasswordError('Password має бути більшим за 8 символів і меншим за 64 ')
		if (!e.target.value) {
			setPasswordError('Password не може бути пустим')
		}
	} else {
		setPasswordError('')
	}
}
